mapper.insert:
/* join-memberInsert */
select member_seq.nextval from dual
INSERT INTO member ( m_num, m_name, m_gender, m_residence, m_phone, m_eMail, m_birth, m_id, m_pwd ) VALUES ( #{m_num}, #{m_name}, #{m_gender}, #{m_residence}, #{m_phone}, #{m_eMail}, #{m_birth}, #{m_id}, #{m_pwd})

mapper.select:
/*join = lookupMember*/
select nvl((
			select 1 from member where m_name=#{m_name} AND m_gender=#{m_gender} AND m_birth=#{m_birth}
			), 0)as state 
from dual
