mapper.update:
update member
set m_phone = #{m_phone}
, m_email = #{m_eMail}
, m_residence = #{m_residence}
where m_num = #{m_num}

update member
set m_pwd = #{m_pwd}
where m_num = #{m_num}

mapper.select:
select nvl((
	select 1 from member
	where m_num = #{m_num} and m_pwd = #{m_pwd}
	), 0)as state
from dual
